import React, { useContext } from "react";
import "./posts.css";
import { StoreContext } from "../../store";

function Posts() {
  const state = useContext(StoreContext);
  const { id } = useParams();
  console.log(state);
  return (
    <div className="posts">
      {state.posts.map((item) => item.id === { id }())}
    </div>
  );
}
export default Posts;
