import React, { useContext } from "react";
import { useParams } from "react-router";
import { StoreContext } from "../../store";

function Post(props) {
  const state = useContext(StoreContext);
  const { id } = useParams();
  const post = state.posts.find((post) => post.id === id);
  return (
    <div>
      hello
      {console.log(post)}
    </div>
  );
}

export default Post;
