        },
      },
      {
        id: "3",
        title: "PANATHINAIKOS",
        description: "This is AEK",
        image: "https://trgtrbtrb.jpg",
        commentsCount: 34,
        user: {
          id: 2345,
          name: "Jon Doe",
          picture: "htts://dvgrtgtrv.jpg",
        },
      },
      {
        id: "4",
        title: "OLYMPIAKOS",
        description: "This is AEK",
        image: "https://trgtrbtrb.jpg",
        commentsCount: 34,
        user: {
          id: 2345,
          name: "Jon Doe",
          picture: "htts://dvgrtgtrv.jpg",
        },
      },
    ],
    addPost: addPost,
  };

  const [state, setState] = useState(initState);

  return (
    <StoreContext.Provider value={state}>
      {props.children}
    </StoreContext.Provider>
  );
};
