import React, { useState } from "react";

export const StoreContext = React.createContext({
  posts: [],
  addPost: () => {},
});

export const StoreContextProvider = (props) => {
  const addPost = (post) => {
    setState({ ...state, posts: [...state.posts, post] });
  };
  const initState = {
    posts: [
      { id: 1, title: "AEK", description: "This is AEK" },
      { id: 2, title: "PAOK", description: "This is PAOK" },
      { id: 3, title: "PAO", description: "This is PAO" },
      { id: 4, title: "OLYMPIAKOS", description: "This is OLYMPIAKOS" },
    ],
    addPost: addPost,
  };

  const [state, setState] = useState(initState);

  return (
    <StoreContext.Provider value={state}>
      {props.children}
    </StoreContext.Provider>
  );
};
